package bd.edu.seu.springbootdemo;import bd.edu.seu.springbootdemo.model.Address;import bd.edu.seu.springbootdemo.model.Phone;import bd.edu.seu.springbootdemo.model.Student;import bd.edu.seu.springbootdemo.repository.StudentRepository;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import java.time.LocalDate;@SpringBootTestpublic class StudentRepositoryTest {    @Autowired    private StudentRepository studentRepository;    @Test    public void createStudent(){        Student student = new Student(1,"Joniyed",new Address(),new Phone(),"joniyed@gmail.com","facebook.com/joniyed",LocalDate.now());        Student student1 = studentRepository.save(student);        Assertions.assertEquals(student,student1);    }}